mapper.update:
update USERINFO
	set password = #{password}
where USER_ID = #{id}

mapper.select:
SELECT COUNT(USER_ID)
FROM USERINFO
WHERE USER_ID = #{id}

mapper.update:
UPDATE USERINFO
SET USER_NAME = #{name},
	EMAIL = #{email},
	NICKNAME = #{nickname},
	AGE = #{age}
WHERE USER_ID = #{id}

UPDATE USERINFO
SET ACTIVE = 0
WHERE USER_ID = #{id}
